package learn.stringconcepts;

public class Trainonstrbasic1 {
	
	
	
	char[] r= {'h','o', 'w'}; //String is array of characters
	
	String qw = new String(r);
	
	String bh="hello"; //String literal
	
	String mn = new String("Welcome"); //new keyword
	
	public void user() {
		
		
	System.out.println(mn);
	System.out.println(r);
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Trainonstrbasic1 gb = new Trainonstrbasic1();
		String vr=gb.bh;
		gb.user();
		System.out.println(vr);
		

	}

}
