package program.practice;

public class Sum_of_Elements_in_a_array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum=0;
		int c[]= {1,2,3,1,1};
		
		for(int i=0;i<c.length;i++) {
			
			sum=sum+c[i];
			
		}
		
		System.out.println(sum);
		
		
		
		
		
		
	}

}
